import { useEffect, useState } from 'react';
import { categories } from '../../data/categories';
import { newDateAdjusted } from '../../helpers/dateFilter';
import { Item } from '../../types/Item';
import * as C from './styles';

type Props = {
  onAdd: (item: Item) => void;
}

export const InsertionArea = ({ onAdd }: Props) => {
  const [date, setDate] = useState('');
  const [category, setCategory] = useState('');
  const [title, setTitle] = useState('');
  const [value, setValue] = useState(0);
  const [isValid, setIsValid] = useState(false);

  useEffect(() => {

  }, [isValid]);

  const validateForm = () => {
    if (!date) {
      console.log('foi');
    }
  }

  const handleAddEvent = () => {
    validateForm();
    if (isValid) {
      const newItem: Item = {
        date: newDateAdjusted(date),
        category: category,
        title: title,
        value: value,
      }
      onAdd(newItem);
      clearFields();
    } else {
      window.alert('Preencha as informações!');
    }
  }

  const clearFields = () => {
    setDate('');
    setCategory('');
    setTitle('');
    setValue(0);
  }

  return (
    <C.Container>
      <C.InputLabel>
        <C.InputTitle>Data</C.InputTitle>
        <C.Input
          type="date"
          value={date}
          onChange={({ target }) => setDate(target.value)}
        />
      </C.InputLabel>

      <C.InputLabel>
        <C.InputTitle>Categoria</C.InputTitle>
        <C.Select value={category} onChange={({ target }) => setCategory(target.value)}>
          <>
            <option></option>
            {Object.keys(categories)
              .map((category) => <option key={category} value={category}>{categories[category].title}</option>)}
          </>
        </C.Select>
      </C.InputLabel>

      <C.InputLabel>
        <C.InputTitle>Título</C.InputTitle>
        <C.Input
          type="text"
          value={title}
          onChange={({ target }) => setTitle(target.value)}
        />
      </C.InputLabel>

      <C.InputLabel>
        <C.InputTitle>Valor</C.InputTitle>
        <C.Input
          type="number"
          value={value}
          onChange={({ target }) => setValue(Number(target.value))}
        />
      </C.InputLabel>

      <C.InputLabel>
        <C.InputTitle>&nbsp;</C.InputTitle>
        <C.Button onClick={handleAddEvent}>Adicionar</C.Button>
      </C.InputLabel>
    </C.Container>
  );
}
