import { useEffect, useState } from 'react';
import { categories } from '../../data/categories';
import { newDateAdjusted } from '../../helpers/dateFilter';
import { Item } from '../../types/Item';
import * as C from './styles';

type Props = {
  onAdd: (item: Item) => void;
}

export const InsertionArea = ({ onAdd }: Props) => {
  const [date, setDate] = useState('');
  const [category, setCategory] = useState('');
  const [title, setTitle] = useState('');
  const [value, setValue] = useState(0);
  const [validateDate, setValidateDate] = useState(true);
  const [validateCategory, setValidateCategory] = useState(true);

  const alertInfo = () => window.alert('Preencha todas as informações!');

  const validateForm = () => {
    if (!date) {
      setValidateDate(false);
      alertInfo();
    }
    if (!category) {
      setValidateCategory(false);
      alertInfo();
    }
    else handleAddEvent();
  }

  useEffect(() => {
    if (!validateDate && date) {
      setValidateDate(true);
    }
  }, [date, validateDate]);

  const handleAddEvent = () => {
    const newItem: Item = {
      date: newDateAdjusted(date),
      category: category,
      title: title,
      value: value,
    }
    onAdd(newItem);
    clearFields();
  }

  const clearFields = () => {
    setDate('');
    setCategory('');
    setTitle('');
    setValue(0);
  }

  return (
    <C.Container>
      <C.InputLabel>
        <C.InputTitle>Data</C.InputTitle>
        <C.Input
          type="date"
          value={date}
          onChange={({ target }) => setDate(target.value)}
          isValid={validateDate}
        />
      </C.InputLabel>

      <C.InputLabel>
        <C.InputTitle>Categoria</C.InputTitle>
        <C.Select
          value={category}
          onChange={({ target }) => setCategory(target.value)}
          isValid={validateCategory}
        >
          <>
            <option></option>
            {Object.keys(categories)
              .map((category) => <option key={category} value={category}>{categories[category].title}</option>)}
          </>
        </C.Select>
      </C.InputLabel>

      <C.InputLabel>
        <C.InputTitle>Título</C.InputTitle>
        <C.Input
          type="text"
          value={title}
          onChange={({ target }) => setTitle(target.value)}
        />
      </C.InputLabel>

      <C.InputLabel>
        <C.InputTitle>Valor</C.InputTitle>
        <C.Input
          type="number"
          value={value}
          onChange={({ target }) => setValue(Number(target.value))}
        />
      </C.InputLabel>

      <C.InputLabel>
        <C.InputTitle>&nbsp;</C.InputTitle>
        <C.Button onClick={validateForm}>Adicionar</C.Button>
      </C.InputLabel>
    </C.Container>
  );
}
